---
title: Rough Database Structure
---
classDiagram
    class Group{
        +String name
        +String description
    }
    class GroupInviteCode{
        +String code
        +DateTime validUntil
    }
    class User{
    }
    note for ShoppingList "(optional)"
    class FoodItem{
        +String name
        +String details // e.g. raw, canned, ...
        +String description
        +String unit
        +float multiples // which can be bought
        +bool shoppinglistIgnore
    }
    class Category{
        +String name
        +String description
    }
    class ListFoodItem{
        +Float quantity
        +Date deadline
    }
    class CustomListItem{
        +String name
        +String details // e.g. only yolk
        +Float quantity
        +DateTime boughtAt
    }
    class RecipeFoodItem{
        +Float quantityPerServing
        +String details // e.g. only yolk
    }
    class Recipe{
        +String name
        +String description
        +String instructions
        +int totalTime // in minutes
        +String image
        +String recipe // formatted
    }
    class BoughtFoodItem{
        +DateTime createdAt
    }
    class RecipeComment{
        +String text
        +DateTime createdAt
    }
    class LikedRecipe{
        +DateTime createdAt
    }
    class Meal{
        +int servings
        +Datetime datetime
    }


    Group "1" <--> "n" User : member of
    Group "1" <--> "n" Recipe
    ListFoodItem "n"  <--> "1" FoodItem
    Meal "n" <--> "1" Recipe : get cooked in
    RecipeFoodItem "n" <--> "1" Recipe: uses
    RecipeFoodItem "n" <--> "1" FoodItem
    Recipe "n"  <--> "n" ListFoodItem
    Category "1"  <--> "n" FoodItem
    Category "1"  <--> "n" CustomListItem
    Recipe "1" <--> "n" RecipeComment
    Recipe "1" <--> "n" LikedRecipe
    LikedRecipe "n" <--> "1" User
    BoughtFoodItem "n" <--> "1" RecipeFoodItem
    BoughtFoodItem "n" <--> "1" Meal
    BoughtFoodItem "n" <--> "1" Group
